import React, { useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import Tooltip from '@material-ui/core/Tooltip';
import CancelIcon from '@material-ui/icons/Cancel';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import EditIcon from '@material-ui/icons/Edit';


const TodoList = () => {

    const [item, setItem] = useState("");
    const[newItem, setNewItem] = useState([]);
    const[toggle, setToggle] = useState(true);
    const[toggleEdit, setToggleEdit] = useState(null);

    const itemList = () => {
        if(!item){
            alert(`please fill the input field.`);
        }else if(item && !toggle){
            setNewItem(
                newItem.map((val) =>{
                    if(val.id === toggleEdit){
                        return {...val, name:item}
                    }
                    return val;
                }) 
                
            )
        }else{
            const allItem = {id: new Date().getTime().toString(), name:item}
            setNewItem([...newItem, allItem]);
            setItem(""); 
        }
    };  

    const deleteItem = (index) =>{
        const updatedItem = newItem.filter((val) =>{
            return index !== val.id;
        })
        setNewItem(updatedItem);
    };

    const[line, setLine] = useState(false);
    const cut =() => {
        setLine(true);
    };

    const removeAll =() =>{
        setNewItem([]);
    }

    const editItem = (id) =>{
        let newEditItem = newItem.find((val) =>{
            return val.id === id;
        });
        setToggle(false);
        setItem(newEditItem.name);
        setToggleEdit(id);
    }

    return(<>

            <div className="main_div">
                <div className="center_div">
                    <br/>
                    <h1> TodoList</h1>
                    <br/>
                    <input type="text" placeholder="Add an Item" onChange={(e)=> setItem(e.target.value)} value={item}/>
                    <Tooltip title="Add Items">
                    <Button className="newBtn" >
                     {
                         toggle ? <AddIcon onClick={itemList}/> : <EditIcon onClick={itemList}/>
                     } 
                    </Button>
                    </Tooltip>
                    <br/>
                    <ol>
                        
                        {newItem.map((val, id) => {
                            return <div className="todo_style" key={val.id}>
                                    <Tooltip title="Cancel Item">
                                    <span onClick={cut}> 
                                    <CancelIcon className="deleteIcon"/>
                                    </span>
                                    </Tooltip>
                                    <Tooltip title="Edit Item">
                                    <span onClick={() => editItem(val.id)}> 
                                    <EditIcon className="deleteIcon"/>
                                    </span>
                                    </Tooltip>
                                    <Tooltip title="Delete Item">
                                    <span onClick={() => deleteItem(val.id)}> 
                                    <DeleteIcon className="deleteIcon"/>
                                    </span>
                                    </Tooltip>
                                    <li style={{textDecoration: line ? "line-through" : "null"}}>{val.name}</li>
                                    </div>
                        })}
                    </ol>
                    <Tooltip title="Remove all Items">
                    <Button className="newBtn1" onClick={removeAll}> <DeleteIcon/></Button>
                    </Tooltip>
                    <br/>
                </div>
            </div>
            </>
    );
};

export default TodoList;
