import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Tooltip from '@material-ui/core/Tooltip';
import TodoList from './component/TodoList';
import DeleteIcon from '@material-ui/icons/Delete';


const App = () => {

    const [item, setItem] = useState("");
    const[newItem, setNewItem] = useState([]);

    const itemEvent = (event) =>{
        setItem(event.target.value);
      };

    const itemList = () => {
        setNewItem((preValue) => {
            return [...preValue, item];
        });
        setItem("");
    };  

    const deleteItem = (id) =>{
        setNewItem((preValue) =>{
            return preValue.filter((arr, index) =>{
                return index !== id;
            })
        })
    };

    const removeAll =() =>{
        setNewItem([]);
    }
    return(
        <>
            <div className="main_div">
                <div className="center_div">
                    <br/>
                    <h1> TodoList</h1>
                    <br/>
                    <input type="text" placeholder="Add an Item" onChange={itemEvent} value={item}/>
                    <Tooltip title="Add Items">
                    <Button className="newBtn" onClick={itemList}> <AddIcon/></Button>
                    </Tooltip>
                    <br/>
                    <ol>

                        {newItem.map((val, index) => {
                            return <TodoList key={index} id={index} onSelect={deleteItem} text={val}/>;
                        })}
                    </ol>
                    <Tooltip title="Remove all Items">
                    <Button className="newBtn" onClick={removeAll}> <DeleteIcon/></Button>
                    </Tooltip>
                    <br/>
                </div>
            </div>
        </>
    ); 
};

export default App;
