import React, { useState } from 'react';

  const App = () => {
  const [fullName, setFullName] = useState({
    fName: "",
    lName: "",
  });

  const inputEvent = (event) => {
    console.log(event.target.value);
    console.log(event.target.name);

    const value = event.target.value;
    const name = event.target.name;
    setFullName((preValue) => {
      if(name === 'fName'){
        return{
          fName: value,
          lName: preValue.lName,
        };
      }else if(name === 'lName'){
        return{
          fName: preValue.fName,
          lName: value,
        };
      }
    });

  };
  const onSubmit = (event) =>{
    event.preventDefault();
    alert('form submitted');
  };

      return (
        <>
        <from onClick={onSubmit}>
        <div>
          <h1> Hello {fullName.fName} {fullName.lName}</h1>
          <input type="text" placeholder="Enter your Name" name="fName" onChange={inputEvent} value={fullName.fName}/><br/>
          <input type="text" placeholder="Enter your Last Name" name="lName" onChange={inputEvent} value={fullName.lName}/>
          <button type="submit" >submit</button>
        </div>
        </from>
        </>
      );
  };

export default App;
