import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Tooltip from '@material-ui/core/Tooltip';
import TodoList from './component/TodoList';


const App = (props) => {

    const [item, setItem] = useState("");
    const[newItem, setNewItem] = useState([]);

    const itemEvent = (event) =>{
        setItem(event.target.value);
      };

    const itemList = () => {
        setNewItem((preValue) => {
            return [...preValue, item];
        });
        setItem("");
    };  

    const deleteItem = (id) =>{
        console.log("deleted");
        setItem((preValue) =>{
            return preValue.filter((arr, index) =>{
                return index !== id;
            })
        })
    };
    return(
        <>
        <div className="main_div">
        <div className="center_div">
            <br/>
            <h1> TodoList</h1>
            <br/>
            <input type="text" placeholder="Add an Item" onChange={itemEvent} value={item}/>
            <Tooltip title="Add Items">
            <Button className="newBtn" onClick={itemList}> <AddIcon/></Button>
            </Tooltip>
            <br/>
            <ol>

                {newItem.map((val, index) => {
                    return <TodoList key={index} id={index} onSelect={deleteItem} text={val}/>;
                })}
            </ol>
            <br/>
        </div>
        </div>
        </>
    ); 
};

export default App;
