import React, { useState } from 'react';

  const App = () => {
  const [fullName, setFullName] = useState({
    fname: "",
    lname: "",
    email: "",
    number:"",
    password:"",
  });
  const inputEvent = (event) => {
    console.log(event.target.value);
    console.log(event.target.name);

    const {value,name} = event.target;
    setFullName((preValue) => {
      console.log(preValue);
      return{
        ...preValue,
        [name]: value,
      }
    });
  };
  const onSubmit = (event) =>{
    event.preventDefault();
    alert('form submitted');
  };
      return (
        <>
        <form onSubmit={onSubmit}>
        <div>
          <h1> Hello {fullName.fname} {fullName.lname}</h1>
          <p> {fullName.email}</p><br/>
          <p> {fullName.nu}</p><br/>
          <input type="text" placeholder="Enter your First Name" name="fname" onChange={inputEvent} value={fullName.fname}/><br/>
          <input type="text" placeholder="Enter your Last Name" name="lname" onChange={inputEvent} value={fullName.lname}/>
          <input type="email" placeholder="Enter your Email" name="email" onChange={inputEvent} value={fullName.email}/>
          <input type="number" placeholder="Enter your Phone Number" name="number" onChange={inputEvent} value={fullName.number}/>
          <input type="password" placeholder="Enter your Password" name="password" onChange={inputEvent} value={fullName.password}/>
          <button type="submit" >submit</button>
        </div>
        </form>
        </> 
      );
  };
export default App;
