import React, { useState } from 'react';

  const App = () => {
  const [fullName, setFullName] = useState({
    fName: "",
    lName: "",
    email: "",
    number:"",
  });

  const inputEvent = (event) => {
    console.log(event.target.value);
    console.log(event.target.name);

    const {value,name} = event.target;
    setFullName((preValue) => {
      if(name === 'fName'){
        return{
          fName: value,
          lName: preValue.lName,
          email: preValue.email,
          number: preValue.number,
        };
      }else if(name === 'lName'){
        return{
          fName: preValue.fName,
          lName: value,
          email: preValue.email,
        number: preValue.number,
        };
      }else if(name === 'email'){
        return{
          fName: preValue.fName,
          lName: preValue.lName,
          email: value,
        number: preValue.number,
        };
      }else if(name === 'phone'){
        return{
          fName: preValue.fName,
          lName: preValue.email,
          email: value,
        number: preValue.number,
        };
      }
    });

  };
  const onSubmit = (event) =>{
    event.preventDefault();
    alert('form submitted');
  };

      return (
        <>
        <from onSubmit={onSubmit}>
        <div>
          <h1> Hello {fullName.fName} {fullName.lName}</h1>
          <input type="text" placeholder="Enter your First Name" name="fName" onChange={inputEvent} value={fullName.fName}/><br/>
          <input type="text" placeholder="Enter your Last Name" name="lName" onChange={inputEvent} value={fullName.lName}/>
          <input type="email" placeholder="Enter your Email" name="lName" onChange={inputEvent} value={fullName.email}/>
          <input type="number" placeholder="Enter your Phone Number" name="lName" onChange={inputEvent} value={fullName.number}/>
          <button type="submit" >submit</button>
        </div>
        </from>
        </>
      );
  };

export default App;
