import React, { useState } from 'react';

  const App = () => {
    const purple = "#8e44ad";
      const [bg, setbg ] = useState(purple);
      const [name, setName] = useState('click me');

    const bgChange = () => {
      let newBg = '#34495e';
      setbg(newBg);
      setName('ayyo');
    };
    const bgBack = () => {
      setbg(purple);
      setName('Ouch');
    };
      return (
        <>
        <div style= {{backgroundColor: bg}}>
          <button onMouseEnter={bgChange} onMouseLeave={bgBack}>{name}</button>
        </div>
        </>
      );
  }

export default App;
