import React, { useState } from 'react';

  const App = () => {
  const [name, setName] = useState("");
  const [lastName, setLastName] = useState("");
  const [fullName, setFullName] = useState();
  const [finalLastName, setFinalLastName] = useState();

  const onSubmit = (event) =>{
    event.preventDefault();
    setFullName(name);
    setFinalLastName(finalLastName);
  };
  const inputEvent = (event) => {
    console.log(event.target.value);
    setName(event.target.value);
  };
  const inputEventTwo = (event) => {
    setLastName(event.target.value);
  };
      return (
        <>
        <from onClick={onSubmit}>
        <div>
          <h1> Hello {fullName} {finalLastName}</h1>
          <input type="text" placeholder="Enter your Name" onChange={inputEvent} value={name}/><br/>
          <input type="text" placeholder="Enter your Last Name" onChange={inputEventTwo} value={lastName}/>
          <button type="submit" >click me</button>
        </div>
        </from>
        </>
      );
  };

export default App;
